INSERT INTO `allocation` VALUES ('42', '测试2', '2017-11-14 11:34:02', '2017-11-14 11:47:17', '0', '20171114/1510631237.pdf', '<p>&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:right\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:48px;font-family:楷体_GB2312\">中石油重庆分公司</span></p><p style=\"text-align:center\"><span style=\"font-size:48px\">CNG</span><span style=\"font-size:48px;font-family:楷体_GB2312\">站控费用管理系统</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:48px;font-family:楷体_GB2312\">建设方案</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\">2010</span></strong><strong><span style=\"font-size:19px;font-family: 楷体_GB2312\">年</span></strong><strong><span style=\"font-size:19px\">6</span></strong><strong><span style=\"font-size:19px;font-family:楷体_GB2312\">月</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p><a name=\"_Toc256170273\"></a>&nbsp;</p><h1><a name=\"_Toc265981301\"></a>1.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体\">现状概述</span></h1><h2><a name=\"_Toc265981302\"></a><a name=\"_Toc256170274\"></a>1.1.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">概述</span></h2><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">根据中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站自身业务特点，结合目前分公司</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">母站的建设，为加强</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">销售管理的需要，进行</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控费用管理的信息化建设。</span></p><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控费用管理系统的建设增强了中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站的收费、记录</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">消费管理功能；同时信息平台站控费用管理系统的一个重要职能是建立了</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站的收支管理平台，让各个</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站可以通过该系统进行</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">的结算与管理功能。</span></p><h2><a name=\"_Toc265981303\"></a><a name=\"_Toc256170275\"></a>1.2.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">项目建设的</span><span style=\"font-family:黑体\">意义</span></h2><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">通过建设</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控费用管理系统，完善分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">销售管理机制，建立起一套符合中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">业务特点的信息化管理模式。</span></p><h2><a name=\"_Toc265981304\"></a>1.3.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">建设原则</span></h2><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><a name=\"_Toc153804186\"></a><a name=\"_Toc153803394\"></a><a name=\"_Toc152383747\"></a><a name=\"_Toc152382999\"></a><a name=\"_Toc152381286\"></a><a name=\"_Toc147166803\"></a><span style=\"font-size:16px;line-height:150%;font-family:宋体\">符合中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控建设要求</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">，考虑到中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控管理的需求特异性，以及需求内部对网络要求的复杂性</span><span style=\"font-size:16px;line-height:150%\">.</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统采用</span><span style=\"font-size:16px;line-height:150%\">B/S</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">结构，发布服务器、应用服务器和数据库服务器放在分公司本部。该系统以</span><span style=\"font-size:16px;line-height:150%\">IE</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">浏览器作为应用客户端，通过统一的入口访问系统，完成各项操作管理。</span></p><h1><a name=\"_Toc265981305\"></a><a name=\"_Toc256170277\"></a>2.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体\">系统总体建设目标</span><a name=\"_Toc257915990\"></a><a name=\"_Toc257917818\"></a><a name=\"_Toc265084001\"></a><a name=\"_Toc265084268\"></a><a name=\"_Toc265098186\"></a><a name=\"_Toc265104321\"></a></h1><h2><a name=\"_Toc265981306\"></a>2.1.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">系统总体目标</span></h2><p><span style=\"font-size:16px;font-family:宋体\">结合</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family:宋体\">母站的建设与管理，建设</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family: 宋体\">站控费用管理系统，实现</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family:宋体\">母站的预收费管理功能，实现</span><span style=\"font-size: 16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family: 宋体\">子站的消费管理、</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family:宋体\">客户关系管理及相应的查询统计功能，并提供与</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">ERP</span><span style=\"font-size:16px;font-family: 宋体\">系统功能的接口标准。</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\"></span></strong><br/></p><p><strong><span style=\"font-size:19px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><table class=table table-striped><thead><tr><th>理财产品名称</th><th>年利率</th><th>起购点</th><th>产品周期</th><th>到期获利</th></tr></thead><tbody><tr><td>超越理财灵活配置B</td><td>5.12</td><td>50000</td><td>15</td><td>2354</td></tr></tbody></table> </span></strong></p><p><strong><span style=\"font-size:19px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><br/></span></strong></p><p><strong><span style=\"font-size:19px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\"><br/><b>投顾姓名:</b>睽违<br/><b>从业证书号:</b>13123123123<br/><b>联系方式:</b>31231231231<br/><b>所属营业部:</b>333</span></strong></p><p><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p><br/></p>', '6', '5', '9', '16');
