INSERT INTO `allocation` VALUES ('44', '测试4', '2017-11-14 11:46:38', '2017-11-14 11:47:12', '0', '20171114/1510631232.pdf', '<p style=\"text-align: center;\"><span style=\"font-size:32px;font-family:黑体\">长江证券麓山大道营业部</span></p><p><span style=\"font-size:32px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size:48px;font-family:黑体\">财富管理资产配置建议书</span></p><p><span style=\"font-size:48px;font-family:黑体\"><br/></span></p><p><span style=\"font-size:48px;font-family:黑体\"></span></p><p>PART.1</p><p>股票类资产配置</p><p>PART.2</p><p>固定收益类产品配置</p><p style=\"text-align: center; \"><span style=\"font-family:黑体\"><span style=\"font-size: 48px;\"><table class=table table-striped><thead><tr><th>理财产品名称</th><th>年利率</th><th>起购点</th><th>产品周期</th><th>到期获利</th></tr></thead><tbody><tr><td>超越理财灵活配置B</td><td>5.12</td><td>50000</td><td>15</td><td>2354</td></tr></tbody></table></span></span></p><table><tbody><tr class=\"firstRow\"><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=