INSERT INTO `altemplate` VALUES ('4', '资产配置建议书', '2017-11-14 11:46:22', '<p style=\"text-align: center;\"><span style=\"font-size:32px;font-family:黑体\">长江证券麓山大道营业部</span></p><p><span style=\"font-size:32px;font-family:黑体\">&nbsp;</span></p><p style=\"text-align: center;\"><span style=\"font-size:48px;font-family:黑体\">财富管理资产配置建议书</span></p><p><span style=\"font-size:48px;font-family:黑体\"><br/></span></p><p><span style=\"font-size:48px;font-family:黑体\"></span></p><p>PART.1</p><p>股票类资产配置</p><p>PART.2</p><p>固定收益类产品配置</p><p style=\"text-align: center; \"><span style=\"font-family:黑体\"><span style=\"font-size: 48px;\">#ppp#</span></span></p><table><tbody><tr class=\"firstRow\"><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr><tr><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td><td width=\"203\" valign=\"top\"><br/></td></tr></tbody></table><p>#adinfo#</p><p><span style=\"font-size:48px;font-family:黑体\"><br/></span><br/></p>', '6', '16', '9');
INSERT INTO `altemplate` VALUES ('5', '建设方案', '2017-11-14 11:35:28', '<p>&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:center\">&nbsp;</p><p style=\"text-align:right\">&nbsp;</p><p style=\"text-align:center\"><span style=\"font-size:48px;font-family:楷体_GB2312\">中石油重庆分公司</span></p><p style=\"text-align:center\"><span style=\"font-size:48px\">CNG</span><span style=\"font-size:48px;font-family:楷体_GB2312\">站控费用管理系统</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:48px;font-family:楷体_GB2312\">建设方案</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:56px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\">2010</span></strong><strong><span style=\"font-size:19px;font-family: 楷体_GB2312\">年</span></strong><strong><span style=\"font-size:19px\">6</span></strong><strong><span style=\"font-size:19px;font-family:楷体_GB2312\">月</span></strong></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p><a name=\"_Toc256170273\"></a>&nbsp;</p><h1><a name=\"_Toc265981301\"></a>1.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体\">现状概述</span></h1><h2><a name=\"_Toc265981302\"></a><a name=\"_Toc256170274\"></a>1.1.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">概述</span></h2><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">根据中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站自身业务特点，结合目前分公司</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">母站的建设，为加强</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">销售管理的需要，进行</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控费用管理的信息化建设。</span></p><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控费用管理系统的建设增强了中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站的收费、记录</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">消费管理功能；同时信息平台站控费用管理系统的一个重要职能是建立了</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站的收支管理平台，让各个</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站可以通过该系统进行</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">的结算与管理功能。</span></p><h2><a name=\"_Toc265981303\"></a><a name=\"_Toc256170275\"></a>1.2.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">项目建设的</span><span style=\"font-family:黑体\">意义</span></h2><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><span style=\"font-size:16px;line-height:150%;font-family:宋体\">通过建设</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控费用管理系统，完善分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">销售管理机制，建立起一套符合中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">业务特点的信息化管理模式。</span></p><h2><a name=\"_Toc265981304\"></a>1.3.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">建设原则</span></h2><p style=\"margin-left:12px;text-indent:28px;line-height: 150%\"><a name=\"_Toc153804186\"></a><a name=\"_Toc153803394\"></a><a name=\"_Toc152383747\"></a><a name=\"_Toc152382999\"></a><a name=\"_Toc152381286\"></a><a name=\"_Toc147166803\"></a><span style=\"font-size:16px;line-height:150%;font-family:宋体\">符合中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控建设要求</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">，考虑到中石油重庆分公司的</span><span style=\"font-size:16px;line-height:150%\">CNG</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">站控管理的需求特异性，以及需求内部对网络要求的复杂性</span><span style=\"font-size:16px;line-height:150%\">.</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">系统采用</span><span style=\"font-size:16px;line-height:150%\">B/S</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">结构，发布服务器、应用服务器和数据库服务器放在分公司本部。该系统以</span><span style=\"font-size:16px;line-height:150%\">IE</span><span style=\"font-size:16px;line-height:150%;font-family:宋体\">浏览器作为应用客户端，通过统一的入口访问系统，完成各项操作管理。</span></p><h1><a name=\"_Toc265981305\"></a><a name=\"_Toc256170277\"></a>2.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-family:宋体\">系统总体建设目标</span><a name=\"_Toc257915990\"></a><a name=\"_Toc257917818\"></a><a name=\"_Toc265084001\"></a><a name=\"_Toc265084268\"></a><a name=\"_Toc265098186\"></a><a name=\"_Toc265104321\"></a></h1><h2><a name=\"_Toc265981306\"></a>2.1.<span style=\"font-variant-numeric: normal;font-weight: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span><span style=\"font-family:黑体\">系统总体目标</span></h2><p><span style=\"font-size:16px;font-family:宋体\">结合</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family:宋体\">母站的建设与管理，建设</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family: 宋体\">站控费用管理系统，实现</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family:宋体\">母站的预收费管理功能，实现</span><span style=\"font-size: 16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family: 宋体\">子站的消费管理、</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">CNG</span><span style=\"font-size:16px;font-family:宋体\">客户关系管理及相应的查询统计功能，并提供与</span><span style=\"font-size:16px;font-family:&#39;Times New Roman&#39;,&#39;serif&#39;\">ERP</span><span style=\"font-size:16px;font-family: 宋体\">系统功能的接口标准。</span></p><p style=\"text-align:center\"><strong><span style=\"font-size:19px\"></span></strong><br/></p><p><strong><span style=\"font-size:19px\">&nbsp;</span></strong></p><p>#ppp#</p><p><br/></p><p>#adinfo#</p>', '6', '16', '9');
INSERT INTO `altemplate` VALUES ('6', '人力资源', '2017-11-14 11:49:28', '<p style=\"text-indent:28px\"><span style=\"font-family:宋体\">整个业务功能分为人力资源管理、财务管理、物资管理和餐饮管理四大模块。每个模块基本功能如下：</span></p><h2><span style=\"font-family:华文中宋\">1.</span><span style=\"font-family: 华文中宋\">人力资源管理</span></h2><h3><span style=\"font-family:华文中宋\">1.1.</span><span style=\"font-family:华文中宋\">组织机构</span></h3><p style=\"text-indent:28px\"><span style=\"font-family:宋体\">信息化平台是基于一体化设计，因此组织机构也是支持一体化组织机构结果的，在组织机构主要包括组织机构、岗位管理。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:56px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">组织机构管理包括：建立组织机构、修改组织机构、换转组织机构、撤销组织机构等操作。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:56px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">岗位管理包括：建立岗位、修改岗位、撤销岗位、岗位与人员对应。</span></p><h3><span style=\"font-family:华文中宋\">1.2.</span><span style=\"font-family:华文中宋\">人员信息</span></h3><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">员工信息录入：记录员工编号、姓名等基本信息，以及所属组织机构、部门等扩展信息，以及家庭成员、学历信息、工作经历等子集信息。支持从外部数据源导入数据。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">员工信息维护：完整记录员工从进入单位、发生变动到离职的全过程信息。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">员工信息分析：各级单位可以动态查询、分析权限内的下级单位的人员数据；预置多种常用员工卡片、花名册、查询统计模板与报表，用户也可以根据自定义各种查询、报表等。可对人才结构进行统计分析，自动生成统计报表和图表，为人才使用和预测提供依据。</span></p><h2><span style=\"font-family:华文中宋\">2.</span><span style=\"font-family: 华文中宋\">物资管理</span></h2><h3><span style=\"font-family:华文中宋\">2.1.</span><span style=\"font-family:华文中宋\">采购管理</span></h3><p><span style=\"font-family:华文中宋\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;</span><span style=\"font-family:宋体\">采购管理包括采购计划、采购订单、采购结算、采购费用、供应商管理、价格及供货信息等业务。各个管理处可以根据自己的实际情况提交采购计划，采购计划通过已设定的工作流进行审批，以保证采购计划的合理性。采购计划审批通过后，总公司的采购部门就可以根据已审核的采购计划选择供应商下达采购订单。采购计划在审批过程中，各个审批环节都可以在采购计划单上查看每个物料的当前库存，以及每个物料的历史采购结算价和历史采购入库价，从而让采购计划的审批更加严谨和有效率，并达到管控的目的。其业务特点如下：</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">支持各个部门分别提交采购计划，汇总后生成管理处或者公司的采购计划。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">支持推单功能，例如可以通过采购计划单生成采购订单，也可以通过采购订单生或采购入库单生成采购结算单。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">采购计划单可以自动显示对应物料的当前即时库存，物料价格可以根据已设置的优先级顺序从最新价格或者物料参考价格中自动携带。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">支持物料和供应商的对应关系，支持采购计划、采购订单等执行情况跟踪。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">支持按照指定方案自动生成采购计划</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><span style=\"font-family:Wingdings\">Ø<span style=\"font-variant-numeric: normal;font-stretch: normal;font-size: 9px;line-height: normal;font-family: &#39;Times New Roman&#39;\">&nbsp; </span></span><span style=\"font-family:宋体\">采购计算能自动生成应付清单到应收应付业务中。</span></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><br/></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\">#ppp#</p><p class=\"MsoListParagraph\" style=\"margin-left:28px\"><br/></p><p class=\"MsoListParagraph\" style=\"margin-left:28px\">#adinfo#</p><p><br/></p>', '6', '16', '9');

